async def show_next_stage(callback: types.CallbackQuery, state: FSMContext):
    data = await state.get_data()
    current_stage = data["current_stage"]
    stages = data["stages"]

    # –£–¥–∞–ª—è–µ–º –ø—Ä–µ–¥—ã–¥—É—â–µ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ —Å –∫–Ω–æ–ø–∫–æ–π
    if data.get("stage_message_id"):
        try:
            await callback.bot.delete_message(
                chat_id=callback.message.chat.id,
                message_id=data["stage_message_id"]
            )
        except:
            pass

    if current_stage < len(stages):
        stage = stages[current_stage]

        text = (
            f"üìå –≠—Ç–∞–ø {stage['number']}: {stage['name']}\n\n"
            f"{stage['description']}"
        )

        if current_stage == 0:
            text = "–≠—Ç–∞–ø—ã –ø—Ä–æ–¥–∞–∂\n\n" + text

        # –°–æ–∑–¥–∞–µ–º –∫–ª–∞–≤–∏–∞—Ç—É—Ä—É
        if current_stage < len(stages) - 1:
            keyboard = InlineKeyboardMarkup(inline_keyboard=[[
                InlineKeyboardButton(text="–î–∞–ª–µ–µ ‚Üí", callback_data="next_stage")
            ]])
        else:
            keyboard = knowledge_theme_keyboard()

        # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ
        msg = await callback.message.answer(
            text,
            parse_mode="Markdown",
            reply_markup=keyboard
        )

        # –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ
        await state.update_data(
            current_stage=current_stage + 1,
            stage_message_id=msg.message_id
        )
    else:
        await start_test(callback, state)


@router.callback_query(F.data == "next_stage")
async def handle_next_stage(callback: types.CallbackQuery, state: FSMContext):
    await show_next_stage(callback, state)
    await callback.answer()

